FlowFile flowFile = session.get()
if (flowFile != null) {
    def attributes = flowFile.getAttributes()

    // Define the base SQL statement for creating the table
    String createTableBaseSql = """
    CREATE TABLE IF NOT EXISTS board
    (
        creationtime character varying,
        machineid character varying,
        measuredafter character varying,
        placeheadid character varying,
        segment_nozzle character varying,
        file_id character varying,
        board_id character varying,
        parsing_timestamp timestamptz default (now() at time zone 'utc'),
        CONSTRAINT board_uniqconstraint UNIQUE (
    """

    // List of columns for unique constraint
    List<String> uniqueColumns = []

    // Add columns based on the attribute values
    if (attributes['IsCreationTimeUnique'] == 'true') {
        uniqueColumns.add('creationtime')
    }
    if (attributes['IsMachineIdUnique'] == 'true') {
        uniqueColumns.add('machineid')
    }
    if (attributes['IsMeasuredAfterUnique'] == 'true') {
        uniqueColumns.add('measuredafter')
    }
    if (attributes['IsPlaceHeadIdUnique'] == 'true') {
        uniqueColumns.add('placeheadid')
    }
    if (attributes['IsSegmentNozzleUnique'] == 'true') {
        uniqueColumns.add('segment_nozzle')
    }

    // Complete the create table SQL statement
    String createTableSql = createTableBaseSql + uniqueColumns.join(', ') + '))'

    // Define the SQL statement to insert data
    String insertSql = """
    INSERT INTO board (
        creationtime,
        machineid,
        measuredafter,
        placeheadid,
        segment_nozzle,
        file_id,
        board_id
    ) VALUES (
        ?,
        ?,
        ?,
        ?,
        ?,
        ?,
        ?
    ) ON CONFLICT (
        ${uniqueColumns.join(', ')}
    ) DO NOTHING;
    """

    // Extract attribute values
    String creationTime = attributes['CreationTime']
    String machineId = attributes['MachineId']
    String measuredAfter = attributes['MeasuredAfter']
    String placeHeadId = attributes['PlaceHeadId']
    String segmentNozzle = attributes['SegmentNozzle']
    String fileId = attributes['filename']
    String boardId = attributes['uuid']

    // JDBC connection settings
    String jdbcUrl = "jdbc:postgresql://localhost:5432/postgres"
    String jdbcUser = "postgres"
    String jdbcPassword = "Luck"

    try {
        // Establish a JDBC connection
        Connection connection = DriverManager.getConnection(jdbcUrl, jdbcUser, jdbcPassword)
        Statement statement = connection.createStatement()

        // Execute the SQL statement to create the table
        statement.executeUpdate(createTableSql)

        // Prepare and execute the SQL statement to insert data
        PreparedStatement preparedStatement = connection.prepareStatement(insertSql)
        preparedStatement.setString(1, creationTime)
        preparedStatement.setString(2, machineId)
        preparedStatement.setString(3, measuredAfter)
        preparedStatement.setString(4, placeHeadId)
        preparedStatement.setString(5, segmentNozzle)
        preparedStatement.setString(6, fileId)
        preparedStatement.setString(7, boardId)

        preparedStatement.executeUpdate()

        // Close the connections
        preparedStatement.close()
        statement.close()
        connection.close()

        // Transfer the flow file to success relationship
        session.transfer(flowFile, REL_SUCCESS)
    } catch (Exception e) {
        log.error("Error executing SQL queries", e)
        session.transfer(flowFile, REL_FAILURE)
    }
}
