import org.apache.commons.io.IOUtils
import java.net.URL
import java.nio.charset.StandardCharsets
import groovy.json.JsonSlurper

def flowFile = session.get()

try {
    def apiUrl = loginUrl.value
    def apiBody = jsonBody.value

    // init https connection
    def conn = new URL(apiUrl).openConnection()

    conn.setRequestMethod("POST")
    conn.setDoOutput(true)
    conn.setRequestProperty("Content-Type", "application/json")
    conn.getOutputStream().write(apiBody.getBytes("UTF-8"))

    // process https response
    def responseCode = conn.getResponseCode()

    if (responseCode >= 200 && responseCode < 300) {
        log.info("Success response from TC Login API")
        def jsonSlurper = new JsonSlurper()
        def responseBody = jsonSlurper.parseText(IOUtils.toString(conn.getInputStream(), StandardCharsets.UTF_8))

        flowFile = session.putAttribute(flowFile, 'token', responseBody.token)
        session.transfer(flowFile, REL_SUCCESS)
    } else {

        log.error("Error response from TC Login API: ${responseCode}")
        flowFile = session.putAttribute(flowFile, 'execution.error', "Error response from TC Login API: ${responseCode} and ${e.message}")
        session.transfer(flowFile, REL_FAILURE)
    }
} catch (Exception e) {
    log.error("Exception response from TC Login API: ${e.message}")
    flowFile = session.putAttribute(flowFile, 'execution.error', "Exception response from TC Login API: ${e.message}")
    session.transfer(flowFile, REL_FAILURE)
}

